import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { saveToken } from './auth';

export default function Register(){
  const [nome,setNome]=useState('');
  const [email,setEmail]=useState('');
  const [senha,setSenha]=useState('');
  const [perfil,setPerfil]=useState('ESTUDANTE');
  const nav = useNavigate();

  const submit = async (e)=>{
    e.preventDefault();
    const res = await fetch('/api/auth/register',{method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({nome,email,senha,perfil})});
    const data = await res.json();
    if(data.token){ saveToken(data.token); nav('/'); }
  }

  return (
    <div style={{padding:20}}>
      <h2>Registrar</h2>
      <form onSubmit={submit}>
        <div><input placeholder='nome' value={nome} onChange={e=>setNome(e.target.value)} required/></div>
        <div><input placeholder='email' value={email} onChange={e=>setEmail(e.target.value)} required/></div>
        <div><input placeholder='senha' type='password' value={senha} onChange={e=>setSenha(e.target.value)} required/></div>
        <div>
          <select value={perfil} onChange={e=>setPerfil(e.target.value)}>
            <option value='ESTUDANTE'>Estudante</option>
            <option value='EMPRESA'>Empresa</option>
          </select>
        </div>
        <button type='submit'>Registrar</button>
      </form>
    </div>
  );
}
